import React from 'react';

class TodoListCard extends React.Component {

    moveUpList = () => {
        console.log("Opened up the list " + this.refs.todoList.id);
    }

    render() {
        const { todoList } = this.props;
        console.log("TodoListCard, todoList.id: " + todoList.id);
        return (
            <div className="card z-depth-0 todo-list-link" ref={todoList} onClick={this.moveUpList}>
                <div className="card-content grey-text text-darken-3">
                    <span className="card-title">{todoList.name}</span>
                </div>
            </div>
        );
    }
}
export default TodoListCard;
